import React, { useEffect, useRef } from 'react';
import { Camera } from 'lucide-react';

const CameraFeed = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
      })
      .catch((error) => console.error('Error accessing camera:', error));
  }, []);

  return (
    <div className="bg-black/30 backdrop-blur-sm rounded-2xl p-4 border border-white/10">
      <div className="aspect-video bg-black/40 rounded-xl overflow-hidden">
        <video ref={videoRef} className="w-full h-full object-cover" autoPlay playsInline muted />
      </div>
      <div className="mt-4 flex justify-between items-center">
        <h2 className="text-lg font-semibold text-white">Live Assembly View</h2>
        <button className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-colors flex items-center gap-2">
          <Camera className="w-4 h-4" />
          Capture
        </button>
      </div>
    </div>
  );
};

export default CameraFeed;
