      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        prompt,
        userId: getUserId(),
        projectId: getProjectId()
      }),
      signal: controller.signal
    });

    clearTimeout(timeoutId);

    if (!response.ok) {
      throw new Error('Failed to generate image');
    }

    // Clean up any existing object URL
    if (generatedImage) {
      URL.revokeObjectURL(generatedImage);
    }

    const blob = await response.blob();
    const imageUrl = URL.createObjectURL(blob);
    setGeneratedImage(imageUrl);

    // Add success message to speech
    setSpeechText(prevText => 
      `${prevText} I've generated a visualization based on your request.`
    );

  } catch (error) {
    if (error.name === 'AbortError') {
      setImageGenerationError('Image generation timed out. Please try again.');
    } else {
      setImageGenerationError(error.message);
    }
    Logger.error('Error generating image:', error);

    // Add error message to speech
    setSpeechText(prevText => 
      `${prevText} I encountered an error while trying to generate the visualization.`
    );
  } finally {
    setIsGeneratingImage(false);
  }
};

