  const handleVoiceInput = async (transcript, statusCallback) => {
    if (isProcessing) {
      Logger.warn('Analysis already in progress, skipping voice input');
      return;
    }

    setIsProcessing(true);
    setSpeechText('');

    try {
      Logger.info('Processing voice input:', transcript);

      // Check if this is a visualization request
      const shouldGenerateImage = isVisualizationRequest(transcript);

      // Rest of your existing handleVoiceInput code...
      const data = await processVoiceQuery(transcript);

      if (data.success) {
        let prompt = '';

        // Only generate image if explicitly requested
        if (shouldGenerateImage) {
          if (data.analysis?.identifiedComponents?.length > 0) {
            // If components are identified, include them in the visualization
            const componentNames = data.analysis.identifiedComponents
              .map(c => typeof c === 'string' ? c : c.name)
              .filter(Boolean)
              .join(', ');

            prompt = `${transcript}. Include these components: ${componentNames}`;
          } else {
            // Use the original request as the prompt
            prompt = transcript;
          }

          Logger.info('Generating visualization based on user request', { prompt });
          generateStableDiffusionImage(prompt);
        }

        // Process the response for speech
        const speechText = processGeminiResponse(data);
        setSpeechText(speechText);

        onAnalysisComplete(data);
      }
    } catch (error) {
      Logger.error('Voice query error:', error);
