// frontend/src/components/MainInterface.js
import React, { useState, useRef, useEffect } from 'react';
import { Clock, MessageSquare, Folder, ArrowLeftRight } from 'lucide-react';
import CameraFeed from './CameraFeed';
import InstructionPanel from './InstructionPanel';
import VisualizationPanel from './VisualizationPanel';
import VoiceInput from './VoiceInput';
import ProjectManager from './ProjectManager';
import NewUserOnboarding from './NewUserOnboarding';
import Logger from '../utils/logger';
import { processGeminiResponse } from '../utils/speechUtils';
import { getUserId, getProjectId } from '../utils/userIdentification';
import { useProject } from '../contexts/ProjectContext';
import GeneratedImagePanel from './GeneratedImagePanel';

const MainInterface = () => {
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState(null);
  const [showProjectManager, setShowProjectManager] = useState(false);
  const [showOnboarding, setShowOnboarding] = useState(false);
  const cameraFeedRef = useRef();
  const [generatedImage, setGeneratedImage] = useState(null);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [imageGenerationError, setImageGenerationError] = useState(null);
  // Get project context
  const { 
    currentProject, 
    isLoading: projectLoading,
    loadActiveProject, 
    setActiveProject,
    addComponents,
    addCompletedStep
  } = useProject();

  // Check for existing project on mount
  useEffect(() => {
    const projectId = getProjectId();
    if (!projectId) {
      // No existing project, show onboarding
      setShowOnboarding(true);
    }
  }, []);

  // Get current step from project
  const currentStep = currentProject?.current_step || 1;

  // Handle completed analysis
  const handleAnalysisComplete = (result) => {
    // Clear the previous analysis result first
    if (!result) {
      setAnalysisResult(null);
      setProcessingStatus(null);
      return;
    }

    if (componentsToStore.length > 0) {
      const componentNames = componentsToStore
        .map(c => typeof c === 'string' ? c : c.name)
        .filter(Boolean)
        .join(', ');

      const prompt = `Create a detailed visualization of a drone with the following components: ${componentNames}`;
      generateStableDiffusionImage(prompt);
    }

    if (result?.analysis?.canProceed) {
      const stepInfo = {
        stepNumber: currentStep,
        title: `Step ${currentStep} completed`,
        timestamp: new Date().toISOString(),
        details: result.analysis.feedback || 'Step completed successfully',
      };

      // Generate visualization for completed step
      const prompt = `Create a visualization of a drone after completing step ${currentStep}: ${stepInfo.details}`;
      generateStableDiffusionImage(prompt);

      // Store the completed step
      addCompletedStep(stepInfo);
    }

    // Set the new analysis result
    setAnalysisResult(result);

    // Log the result to see what components are being identified
    Logger.info('Analysis completed', {
      hasData: !!result,
      hasAnalysis: !!result?.analysis,
      identifiedComponents: !!result?.analysis?.identifiedComponents,
      identifiedParts: !!result?.analysis?.identifiedParts,
      timestamp: new Date().toISOString() // Add timestamp for debugging
    });

    // If we have project context and identified components, store them
    if (currentProject) {
      // Handle different response formats
      let componentsToStore = [];

      if (result?.analysis?.identifiedComponents && Array.isArray(result.analysis.identifiedComponents)) {
        componentsToStore = result.analysis.identifiedComponents;
      } else if (result?.analysis?.identifiedParts && Array.isArray(result.analysis.identifiedParts)) {
        componentsToStore = result.analysis.identifiedParts;
      }

      if (componentsToStore.length > 0) {
        Logger.info('Found components to store', { count: componentsToStore.length });
        storeIdentifiedComponents(componentsToStore);
      }
    }

    // If analysis indicates we can proceed to next step
    if (result?.analysis?.canProceed) {
      const stepInfo = {
        stepNumber: currentStep,
        title: `Step ${currentStep} completed`,
        timestamp: new Date().toISOString(),
        details: result.analysis.feedback || 'Step completed successfully',
      };

      // Store the completed step
      addCompletedStep(stepInfo);
    }
  };

  // Store identified components
  const storeIdentifiedComponents = async (components) => {
    if (!components || !Array.isArray(components) || components.length === 0) return;

    try {
      Logger.info('Storing identified components', { count: components.length });
      await addComponents(components);
    } catch (error) {
      Logger.error('Error storing components', error);
    }
  };

  const generateStableDiffusionImage = async (prompt) => {
    try {
      setIsGeneratingImage(true);
      setImageGenerationError(null);

      const response = await fetch('http://localhost:5003/api/assembly/visualize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          prompt,
          userId: getUserId(),
          projectId: getProjectId()
        })
      });

      if (!response.ok) {
        throw new Error('Failed to generate image');
      }

      const data = await response.json();
      if (data.success) {
        setGeneratedImage(data.image);
      } else {
        throw new Error(data.error || 'Image generation failed');
      }
    } catch (error) {
      Logger.error('Error generating image:', error);
      setImageGenerationError(error.message);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // Handle voice input
  const handleVoiceInput = async (transcript, statusCallback) => {
    if (!cameraFeedRef.current) {
      Logger.error('Camera feed not initialized');
      statusCallback('Error: Camera not ready');
      return;
    }

    // Clear previous analysis result
    setAnalysisResult(null);
    setIsProcessing(true);
    Logger.info('Processing voice input:', transcript);
    setProcessingStatus('Capturing image...');

    try {
      // Get current frame from camera
      const imageData = cameraFeedRef.current.captureFrame();

      if (!imageData) {
        throw new Error('Failed to capture camera frame');
      }

      Logger.info('Image captured successfully');
      setProcessingStatus('Image captured ✅ Sending to API...');
      statusCallback('Image captured ✅ Sending to API...');

      // Get user and project IDs
      const userId = getUserId();
      const projectId = getProjectId();

      // Send to backend with detailed logging
      Logger.info('Sending voice query to backend', {
        step: currentStep,
        query: transcript,
        imageSize: imageData.length,
        userId,
        projectId,
        requestId: Date.now().toString() // Add unique request ID
      });

      if (data.success && data.analysis?.identifiedComponents?.length > 0) {
        const componentNames = data.analysis.identifiedComponents
          .map(c => typeof c === 'string' ? c : c.name)
          .filter(Boolean)
          .join(', ');

        const prompt = `Create a detailed visualization of a drone components: ${componentNames}`;
        generateStableDiffusionImage(prompt);
      }
      const response = await fetch('http://localhost:5003/api/assembly/gemini/voice-query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          image: imageData,
          query: transcript,
          currentStep,
          userId,
          projectId,
          requestId: Date.now().toString() // Add unique request ID
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      Logger.info('Voice query response received', {
        success: data.success,
        status: data.analysis?.status,
        hasContext: data.hasContext,
        timestamp: new Date().toISOString()
      });

      setProcessingStatus('Response received ✅');
      statusCallback('Response received ✅');

      if (data.success) {
        // Update the analysis result
        setAnalysisResult(data);

        // If we have identified components, store them
        if (data.analysis?.identifiedComponents) {
          storeIdentifiedComponents(data.analysis.identifiedComponents);
        }

        // Process the response for speech
        const processedResponse = processGeminiResponse(data);
        Logger.info('Processing voice response for speech:', {
          text: processedResponse.slice(0, 100) + (processedResponse.length > 100 ? '...' : ''),
          timestamp: new Date().toISOString()
        });

        // Set speech text on CameraFeed
        if (cameraFeedRef.current && typeof cameraFeedRef.current.setSpeechText === 'function') {
          // Clear previous speech first
          cameraFeedRef.current.setSpeechText('');

          // Use a slight delay to ensure state updates don't conflict
          setTimeout(() => {
            cameraFeedRef.current.setSpeechText(processedResponse);
          }, 100);
        }
      } else {
        throw new Error(data.error || 'Voice query failed');
      }

    } catch (error) {
      Logger.error('Voice query error:', error);
      setProcessingStatus('Error processing request ❌');
      statusCallback('Error processing request ❌');

      // Clear analysis result on error
      setAnalysisResult(null);

      // Set error message for speech
      if (cameraFeedRef.current && typeof cameraFeedRef.current.setSpeechText === 'function') {
        cameraFeedRef.current.setSpeechText('I encountered an error processing your question. Please try again.');
      }
    } finally {
      setIsProcessing(false);
    }
  };

  // Handle project selection
  const handleProjectSelect = (project) => {
    setActiveProject(project);
    setShowProjectManager(false);
  };

  // Handle onboarding completion
  const handleOnboardingComplete = () => {
    setShowOnboarding(false);
    loadActiveProject();
  };

  // Toggle project manager
  const toggleProjectManager = () => {
    setShowProjectManager(!showProjectManager);
  };

  // Render content based on state
  const renderContent = () => {
    if (showOnboarding) {
      return (
        <div className="container mx-auto px-4 py-8">
          <NewUserOnboarding 
            onComplete={handleOnboardingComplete}
            onGenerateImage={generateStableDiffusionImage} 
          />
        </div>
      );
    }

    if (projectLoading) {
      return (
        <div className="container mx-auto px-4 py-8 flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
        </div>
      );
    }

    return (
      <main className="container mx-auto px-4 py-6">
        {showProjectManager ? (
          <ProjectManager 
            onProjectSelect={handleProjectSelect}
            onNewProject={handleProjectSelect}
          />
        ) : (
          <div className="grid grid-cols-12 gap-6">
            <div className="col-span-12 lg:col-span-8">
              <CameraFeed 
                ref={cameraFeedRef}
                currentStep={currentStep}
                onAnalysisComplete={handleAnalysisComplete}
              />

              {processingStatus && (
                <div className="mt-4 bg-black/50 text-white p-4 rounded-xl">
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                    <span>{processingStatus}</span>
                  </div>
                </div>
              )}
            </div>
            <div className="col-span-12 lg:col-span-4 space-y-6">
              <InstructionPanel 
                currentStep={currentStep}
                analysis={analysisResult?.analysis}
                project={currentProject}
              />
              <VisualizationPanel 
                components={currentProject?.components || []}
              />
              <GeneratedImagePanel
                image={generatedImage}
                isLoading={isGeneratingImage}
                error={imageGenerationError}
                title="Drone Visualization"
              />
            </div>
          </div>
        )}
      </main>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-indigo-800">
      <header className="border-b border-white/10 backdrop-blur-md bg-black/20">
        <div className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-white">
              {currentProject?.project_name || 'Drone Assembly'}
            </h1>
            <div className="flex items-center gap-4">
              <button 
                onClick={toggleProjectManager}
                className="text-indigo-200 hover:text-white transition-colors"
                title="Projects"
              >
                <Folder className="w-6 h-6" />
              </button>
              <button className="text-indigo-200 hover:text-white transition-colors" title="History">
                <Clock className="w-6 h-6" />
              </button>
              <button className="text-indigo-200 hover:text-white transition-colors" title="Chat">
                <MessageSquare className="w-6 h-6" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {renderContent()}

      <div className="fixed bottom-6 right-6 flex flex-col gap-4">
        <VoiceInput 
          onVoiceInput={handleVoiceInput}
          disabled={isProcessing || showProjectManager || showOnboarding}
        />
        <button 
          className="w-14 h-14 bg-indigo-600 hover:bg-indigo-700 rounded-full flex items-center justify-center text-white shadow-lg transition-colors"
          onClick={toggleProjectManager}
          title={showProjectManager ? "Back to Assembly" : "Switch Project"}
        >
          <ArrowLeftRight className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
};

export default MainInterface;
