import React from 'react';
import { Image } from 'lucide-react';

const InstructionPanel = () => {
  return (
    <div className="bg-black/30 backdrop-blur-sm rounded-2xl p-4 border border-white/10">
      <h2 className="text-lg font-semibold text-white mb-4">Current Step</h2>
      <div className="space-y-4">
        <div className="bg-black/20 rounded-xl p-4">
          <p className="text-indigo-200">Position the flight controller in the center frame...</p>
        </div>
        <div className="aspect-video bg-black/40 rounded-xl flex items-center justify-center">
          <Image className="w-8 h-8 text-indigo-300" />
        </div>
      </div>
    </div>
  );
};

export default InstructionPanel;
