// frontend/src/components/MainInterface.js
import React, { useState } from 'react';
import { Clock, MessageSquare, Play } from 'lucide-react';
import CameraFeed from './CameraFeed';
import InstructionPanel from './InstructionPanel';
import VisualizationPanel from './VisualizationPanel';
import VoiceInput from './VoiceInput';

const MainInterface = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleAnalysisComplete = (result) => {
    setAnalysisResult(result);
    // If analysis indicates we can proceed to next step
    if (result.analysis?.canProceed) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleVoiceInput = async (transcript) => {
    setIsProcessing(true);
    try {
      // We'll use the CameraFeed reference to handle the voice query
      if (window.handleVoiceInputQuery) {
        await window.handleVoiceInputQuery(transcript);
      }
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-indigo-800">
      <header className="border-b border-white/10 backdrop-blur-md bg-black/20">
        <div className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-white">Drone Assembly</h1>
            <div className="flex items-center gap-4">
              <button className="text-indigo-200 hover:text-white transition-colors">
                <Clock className="w-6 h-6" />
              </button>
              <button className="text-indigo-200 hover:text-white transition-colors">
                <MessageSquare className="w-6 h-6" />
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6">
        <div className="grid grid-cols-12 gap-6">
          <div className="col-span-12 lg:col-span-8">
            <CameraFeed 
              currentStep={currentStep}
              onAnalysisComplete={handleAnalysisComplete}
            />
          </div>
          <div className="col-span-12 lg:col-span-4 space-y-6">
            <InstructionPanel 
              currentStep={currentStep}
              analysis={analysisResult?.analysis}
            />
            <VisualizationPanel />
          </div>
        </div>
      </main>

      <div className="fixed bottom-6 right-6 flex flex-col gap-4">
        <VoiceInput 
          onVoiceInput={handleVoiceInput}
          disabled={isProcessing}
        />
        <button className="w-14 h-14 bg-indigo-600 hover:bg-indigo-700 rounded-full flex items-center justify-center text-white shadow-lg transition-colors">
          <Play className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
};

export default MainInterface;
