// Updated MainInterface.js
import React, { useState, useRef } from 'react';
import { Clock, MessageSquare, Play } from 'lucide-react';
import CameraFeed from './CameraFeed';
import InstructionPanel from './InstructionPanel';
import VisualizationPanel from './VisualizationPanel';
import VoiceInput from './VoiceInput';
import Logger from '../utils/logger';
import { processGeminiResponse } from '../utils/speechUtils';

const MainInterface = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState(null);
  const cameraFeedRef = useRef();

// Updated handleVoiceInput function for MainInterface.js

const handleVoiceInput = async (transcript, statusCallback) => {
  if (!cameraFeedRef.current) {
    Logger.error('Camera feed not initialized');
    statusCallback('Error: Camera not ready');
    return;
  }

  setIsProcessing(true);
  Logger.info('Processing voice input:', transcript);
  setProcessingStatus('Capturing image...');

  try {
    // Get current frame from camera
    const imageData = cameraFeedRef.current.captureFrame();

    if (!imageData) {
      throw new Error('Failed to capture camera frame');
    }

    Logger.info('Image captured successfully');
    setProcessingStatus('Image captured ✅ Sending to API...');
    statusCallback('Image captured ✅ Sending to API...');

    // Send to backend with detailed logging
    Logger.info('Sending voice query to backend', {
      step: currentStep,
      query: transcript,
      imageSize: imageData.length
    });

    const response = await fetch('http://localhost:5003/api/assembly/gemini/voice-query', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        image: imageData,
        query: transcript,
        currentStep
      })
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    Logger.info('Voice query response received', {
      success: data.success,
      status: data.analysis?.status
    });

    setProcessingStatus('Response received ✅');
    statusCallback('Response received ✅');

    if (data.success) {
      // Update the analysis result
      setAnalysisResult(data);

      // Process the response for speech - THIS IS THE KEY ADDITION
      // Call the cameraFeed's handleVoiceInput with the transcript
      // This ensures the text-to-speech functionality works
      if (cameraFeedRef.current && typeof cameraFeedRef.current.handleVoiceInput === 'function') {
        Logger.info('Forwarding voice response to CameraFeed for speech processing');
        cameraFeedRef.current.handleVoiceInput(transcript);
      } else {
        // If the direct reference isn't available, process the speech response here
        const processedResponse = processGeminiResponse(data);
        Logger.info('Processing voice response for speech', {
          text: processedResponse.slice(0, 100) + (processedResponse.length > 100 ? '...' : '')
        });

        // Set speech text on CameraFeed
        if (cameraFeedRef.current && typeof cameraFeedRef.current.setSpeechText === 'function') {
          cameraFeedRef.current.setSpeechText(processedResponse);
        }
      }
    } else {
      throw new Error(data.error || 'Voice query failed');
    }

  } catch (error) {
    Logger.error('Voice query error:', error);
    setProcessingStatus('Error processing request ❌');
    statusCallback('Error processing request ❌');

    // Set error message for speech
    if (cameraFeedRef.current && typeof cameraFeedRef.current.setSpeechText === 'function') {
      cameraFeedRef.current.setSpeechText('I encountered an error processing your question. Please try again.');
    }
  } finally {
    setIsProcessing(false);
  }
};

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-indigo-800">
      <header className="border-b border-white/10 backdrop-blur-md bg-black/20">
        <div className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-white">Drone Assembly</h1>
            <div className="flex items-center gap-4">
              <button className="text-indigo-200 hover:text-white transition-colors">
                <Clock className="w-6 h-6" />
              </button>
              <button className="text-indigo-200 hover:text-white transition-colors">
                <MessageSquare className="w-6 h-6" />
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6">
        <div className="grid grid-cols-12 gap-6">
          <div className="col-span-12 lg:col-span-8">
            <CameraFeed 
              ref={cameraFeedRef}
              currentStep={currentStep}
              onAnalysisComplete={handleAnalysisComplete}
            />

            {processingStatus && (
              <div className="mt-4 bg-black/50 text-white p-4 rounded-xl">
                <div className="flex items-center gap-2">
                  <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                  <span>{processingStatus}</span>
                </div>
              </div>
            )}
          </div>
          <div className="col-span-12 lg:col-span-4 space-y-6">
            <InstructionPanel 
              currentStep={currentStep}
              analysis={analysisResult?.analysis}
            />
            <VisualizationPanel />
          </div>
        </div>
      </main>

      <div className="fixed bottom-6 right-6 flex flex-col gap-4">
        <VoiceInput 
          onVoiceInput={handleVoiceInput}
          disabled={isProcessing}
        />
        <button className="w-14 h-14 bg-indigo-600 hover:bg-indigo-700 rounded-full flex items-center justify-center text-white shadow-lg transition-colors">
          <Play className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
};

export default MainInterface;
